component.script:
function init() as void m.top.backgroundUri = "pkg:/images/splash_$$RES$$.jpg" m.lbl = m.top.FindNode("lbl") m.segmentLib = CreateObject("roSGNode", "SegmentLibrary")
m.messageButtons = m.top.FindNode("messageButtons") m.messageButtons.setFocus(true) m.messageButtons.observeField("itemSelected", "onItemSelected") end function
sub onItemSelected(event) itemSelected = event.getData() item = m.messageButtons.content.getChild(itemSelected) m.global.Segment_analytics.message = {"event": item.id} m.lbl.text = "Message " + chr(34) + item.id + chr(34) + " was sent" ?m.global.Segment_analytics.message end sub
